                        <Image src={myth?.slides[0]} />
                        <div>
                            <Title>{myth?.desc[lang].title}</Title>
                            <Subtitle>{myth?.desc[lang].one.subtitle}</Subtitle>
                            <DescWrapper desc={myth?.desc[lang].one.body} TextWrapper={TextWrapper} />
                        </div>
                    </BoxWrapper>
                </MythContainer>
            </PageContainer>
        );
    }
}
