import React, { useEffect, useState, useRef } from 'react';
import { useSetLang } from "../../../App";

import { ArrowForwardIos } from "@mui/icons-material";

import {
    StyledButton,
    Arrow,
    ImgInfo,
    ImageContainer,
    ImageWrapper,
    ButtonWrapper,
} from './poetBioStyles';

export default function Slideshow({
    parentRef,
    screenSize
}) {
    const { lang } = useSetLang();
    const [translate, setTranslate] = useState(0);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [infoArr, setInfoArr] = useState([...slides]);

    useEffect(() => {
        const parentWidth = parentRef?.current?.getBoundingClientRect().width;

        setScreenSize(parentWidth);
    }, []);

    useEffect(() => {
        setInfoArr([...slides]);
    }, [lang]);

    const movePrev = () => {
        if (currentIndex > 0) {
            setCurrentIndex(prevState => prevState - 1);
            setTranslate(prevState => prevState + screenSize);
        }
    };

    const moveNext = () => {
        if (currentIndex < infoArr?.length - 1) {            
            setCurrentIndex(prevState => prevState + 1);
            setTranslate(prevState => prevState - screenSize);
        }
    };

    return (
        <SlideContainer ref={parentRef}>
            <ImageContainer>
                {infoArr?.map((entry, i) => {
                    return (
                        <ImageWrapper key={i + 'b'} src={entry?.img} width={screenSize} translate={translate}>
                            <ImgInfo dangerouslySetInnerHTML={{__html: entry?.text}} />
                        </ImageWrapper>
                    )
                })}
            </ImageContainer>
            <ButtonWrapper>
                <StyledButton left={true} onClick={movePrev} disabled={currentIndex === 0}>
                    <Arrow>
                        <ArrowForwardIos style={{marginLeft: '1px'}}/>
                    </Arrow>
                </StyledButton>
                <StyledButton onClick={moveNext} disabled={currentIndex === infoArr?.length - 1}>
                    <Arrow>
                        <ArrowForwardIos />
                    </Arrow>
                </StyledButton>
            </ButtonWrapper>
        </SlideContainer>
    )
}
