                        <BoxWrapper>
                            <Image src={myth?.slides[i]} />
                            <TextContainer>
                                {i === 0 && <Title>{entry[lang].title}</Title>}
                                <Subtitle>{entry[lang][i].subtitle}</Subtitle>
                                <DescWrapper desc={entry[lang][i].body} TextWrapper={TextWrapper} />
