import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSetLang } from "../../App";

import { DescWrapper } from "../../common/descWrapper/DescWrapper";

import { 
    BoxWrapper,
    Image,
    FirstBox,
    Subtitle,
    TextContainer
} from "./mythPageStyles";

export default function BoxOne({ img, subtitle, body}) {
    const { id } = useParams();
    const { lang } = useSetLang();

    return (
        <BoxWrapper>
            <Image src={img} />
            <TextContainer>
                {/* {i === 0 && <Title>{entry.title}</Title>} */}
                <Subtitle>{subtitle}</Subtitle>
                <DescWrapper desc={body} TextWrapper={FirstBox} />
            </TextContainer>
        </BoxWrapper>
    );
}
