import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSetLang } from "../../App";

import { requestMyth } from "../../services/request";
import myth from "./creature.json";

import { DescWrapper } from "../../components/common/descWrapper/DescWrapper";

import { 
    PageContainer,
    MythContainer,
    BoxWrapper,
    Image,
    Title,
    Subtitle,
    TextWrapper,
    TextContainer
} from "./mythPageStyles";

export default function MythPage() {
    const { id } = useParams();
    const { lang } = useSetLang();
    // const [myth, setMyth] = useState();

    // const fetchData = async () => {
    //     try {
    //         const data = await requestMyth(id);
    //         setMyth(data);
    //     } catch (error) {
    //         console.error("Error fetching data:", error);
    //     }
    // };

    // useEffect(() => {
    //     // Get data
    //     fetchData();
    // }, []);

    if (myth) {
        return (
            <PageContainer>
                <MythContainer>
                    {myth?.desc.map((entry, i) => {
                        <BoxWrapper>
                            {i === 0 && <Image src={myth?.slides[0]} />}
                            <TextContainer>
                                <Title>{entry[lang].title}</Title>
                                <Subtitle>{entry[lang].one.subtitle}</Subtitle>
                                <DescWrapper desc={entry[lang].one.body} TextWrapper={TextWrapper} />
                            </TextContainer>
                        </BoxWrapper>
                    })}
                    <BoxWrapper>
                        <Image src={myth?.slides[0]} />
                        <TextContainer>
                            <Title>{myth?.desc[lang].title}</Title>
                            <Subtitle>{myth?.desc[lang].one.subtitle}</Subtitle>
                            <DescWrapper desc={myth?.desc[lang].one.body} TextWrapper={TextWrapper} />
                        </TextContainer>
                    </BoxWrapper>
                    <BoxWrapper>
                        <Image src={myth?.slides[1]} />
                        <TextContainer>
                            <Subtitle>{myth?.desc[lang].two.subtitle}</Subtitle>
                            <DescWrapper desc={myth?.desc[lang].two.body} TextWrapper={TextWrapper} />
                        </TextContainer>
                    </BoxWrapper>
                    <BoxWrapper>
                        <Image src={myth?.slides[2]} />
                        <TextContainer>
                            <Subtitle>{myth?.desc[lang].three.subtitle}</Subtitle>
                            <DescWrapper desc={myth?.desc[lang].three.body} TextWrapper={TextWrapper} />
                        </TextContainer>
                    </BoxWrapper>
                    <BoxWrapper>
                        <Image src={myth?.slides[3]} />
                        <TextContainer>
                            <Subtitle>{myth?.desc[lang].four.subtitle}</Subtitle>
                            <DescWrapper desc={myth?.desc[lang].four.body} TextWrapper={TextWrapper} />
                        </TextContainer>
                    </BoxWrapper>
                </MythContainer>
            </PageContainer>
        );
    }
}
