import React, { useState, useEffect, useRef } from "react";
import { useSetLang } from "../../../App";

import { ArrowForwardIos } from "@mui/icons-material";

import { DescWrapper } from "../../common/descWrapper/DescWrapper";

import { 
    BoxWrapper,
    Image,
    Title,
    FirstBox,
    Subtitle,
    TextContainer,
    ImgInfo,
    ImageWrapper,
} from "./mythIntroStyles";

import { ButtonWrapper, StyledButton, Arrow } from "../../poet/poetBio/poetBioStyles";

export default function BoxOne({ myth }) {
    const { lang } = useSetLang();
    const parentRef = useRef(null);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [screenSize, setScreenSize] = useState(0);
    const [translate, setTranslate] = useState(0);
    const [infoArr, setInfoArr] = useState([...myth?.slides]);

    useEffect(() => {
        const parentWidth = parentRef?.current?.getBoundingClientRect().width;

        setScreenSize(parentWidth);
    }, []);

    useEffect(() => {
        setInfoArr([...myth?.slides]);
    }, [lang]);

    const movePrev = () => {
        if (currentIndex > 0) {
            setCurrentIndex(prevState => prevState - 1);
            setTranslate(prevState => prevState + screenSize);
        }
    };

    const moveNext = () => {
        if (currentIndex < infoArr?.length - 1) {            
            setCurrentIndex(prevState => prevState + 1);
            setTranslate(prevState => prevState - screenSize);
        }
    };

    return (
        <BoxWrapper reverse={0}>
            <ImageWrapper ref={parentRef}>
                {myth?.slides.map((entry, i) => {
                    return (
                        <Image key={i + 'c'} src={entry?.src} width={screenSize} translate={translate}>
                            <ImgInfo dangerouslySetInnerHTML={{ __html: entry?.info}} />
                        </Image>
                    )
                })}
                <ButtonWrapper>
                    <StyledButton left={true} onClick={movePrev} disabled={currentIndex === 0}>
                        <Arrow>
                            <ArrowForwardIos style={{marginLeft: '1px'}}/>
                        </Arrow>
                    </StyledButton>
                    <StyledButton onClick={moveNext} disabled={currentIndex === infoArr?.length - 1}>
                        <Arrow>
                            <ArrowForwardIos />
                        </Arrow>
                    </StyledButton>
                </ButtonWrapper>
            </ImageWrapper>
            <TextContainer>
                <Title>{myth.title}</Title>
                <Subtitle>{myth.sections[0].subtitle}</Subtitle>
                <DescWrapper desc={myth.sections[0].body} TextWrapper={FirstBox} />
            </TextContainer>
        </BoxWrapper>
    );
}
