import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSetLang } from "../../App";

import { requestMyth } from "../../services/request";

import { DescWrapper } from "../../components/common/descWrapper/DescWrapper";

import { 
    PageContainer,
    MythContainer,
    BoxWrapper,
    Image,
    Title,
    Subtitle,
    TextWrapper
} from "./mythPageStyles";

export default function MythPage() {
    const { id } = useParams();
    const { lang } = useSetLang();
    const [myth, setMyth] = useState();

    const fetchData = async () => {
        try {
            const data = await requestMyth(id);
            setMyth(data);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    useEffect(() => {
        // Get data
        fetchData();
    }, []);

    if (myth) {
        return (
            <PageContainer>
                <MythContainer>
                    <BoxWrapper>
                        <Image src={myth?.slides[0]} />
                        <div>
                            <Title>{myth?.desc[lang].title}</Title>
                            <Subtitle>{myth?.desc[lang].one.title}</Subtitle>
                            <DescWrapper desc={myth?.desc[lang].one.body} TextWrapper={TextWrapper} />
                        </div>
                    </BoxWrapper>
                </MythContainer>
            </PageContainer>
        );
    }
}
